{
    EjsType     *type;

    type = ejs->frameType = ejsCreateNativeType(ejs, N("ejs", "Frame"), ES_Frame, sizeof(EjsFrame), manageFrame, 
        EJS_POT_HELPERS);
    type->constructor.block.pot.shortScope = 1;
}


